CREATE TABLE users (	id SERIAL PRIMARY KEY,	name VARCHAR(100) NOT NULL,	email VARCHAR(40) NOT NULL,	password VARCHAR(200) NOT NULL,	created_on DATE NOT NULL	last_login DATE NOT NULL	)			CREATE TABLE books (	id SERIAL PRIMARY KEY,	isbn VARCHAR(100) NOT NULL,	title VARCHAR(255) NOT NULL,	author VARCHAR(100) NOT NULL,	p_year DATE NOT NULL	reviews_id INTEGER NOT NULL FOREIGN KEY "books.id"	)	CREATE TABLE reviews (	id SERIAL PRIMARY KEY,	reviewer VARCHAR(100) NOT NULL,	review_date DATE NOT NULL	review VARCHAR(255) 	books_id INTEGER NOT NULL FOREIGN KEY "books.id"	)